mapper.update:
UPDATE account
SET  name = #{name}, hp = #{hp}, addrs = #{addrs}, postcode = #{postcode}
WHERE acode = #{acode}

mapper.select:
SELECT acode, id, pw, name, hp, addrs, postcode, astatus
FROM  account
WHERE acode = #{acode}

mapper.update:
UPDATE account
SET
pw=#{pw}, #{name}, #{hp}, #{addrs}, #{postcode}, #{astatus}
WEHRE
acode=#{acode}
